sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    // URLで渡されるuser_nameパスパラメータの値を取得
	var requestUser = request.pathParams.user_name;
	// URLで渡されるni_queryクエリパラメータの値を取得
	var requestNIQuery = request.queryParams.ni_query;

	// sys_userテーブルをGlideRecordで取得
	var requestUserName = new GlideRecord('sys_user');
	// user_idパスパラメータで渡されたユーザのレコードを取得	
	requestUserName.get('user_name', requestUser);

	// user_nameパスパラメータで渡されたuser_nameのレコードがない場合、カスタムエラーを返す
	// エラーオブジェクトは、sn_ws_err.ServiceError();から作成される
	// ステータス値が設定されていない場合、HTTPステータスは500になる
	if(!requestUserName.user_name) {
		var apiError = new sn_ws_err.ServiceError();
		apiError.setStatus(542);
		apiError.setMessage("User not found");
		apiError.setDetail("No user record found for the user _name passed into the ni_info web service resourse using the user_name path parameter.");
		response.setError(apiError);
		return;
	}

	// u_requested_forの値がuser_nameパスパラメータのユーザである
	// アクティブレコードの数を取得
	// GlideAaggregate：データベース集計クエリ(AVG, COUNT, MAX, MIN, etc...)
	var userNICount = new GlideAggregate('x_58872_needit_needit');
	// レコード数をカウント
	userNICount.addAggregate('COUNT');
	// u_requested_for = requestUserName.sys_id のレコードを抽出
	userNICount.addQuery('u_requested_for', requestUserName.sys_id);
	// エンコードされたクエリはクエリパラメータの関連付けから取得される
	userNICount.addEncodedQuery(requestNIQuery);
	// request_typeごとにグループ化
	userNICount.groupBy('u_request_type');
	// クエリの実行
	userNICount.query();

	var needitRecs = 0;
	var niUserRecs = [];
	var needitRecsCount = 0;

	while(userNICount.next()) {
		needitRecs = userNICount.getAggregate('COUNT');
		var reqType = userNICount.u_request_type.getDisplayValue();
		niUserRecs.push({"requestType": reqType, "count": needitRecs});
		needitRecsCount = parseInt(needitRecsCount) + parseInt(needitRecs);
	}

	// bodyオブジェクトを作成
	// bodyにプロパティ値のペアを追加
	var body = {};
	body.totalUserNIRecos = needitRecsCount;
	body.catCounts = niUserRecs;
	body.user = {"User name": requestUserName.user_name, "User ID": requestUserName.sys_id};	

	// 要求元に返される応答オブジェクトをbodyオブジェクトに送信
	response.setBody(body);

})(request, response);
